import React from 'react';
import ReactDOM from 'react-dom';
// import json file
import data from "./data";
// import stylesheet
import 'bulma/css/bulma.css'

 // [{user},{user}, ...]
const users = data.results;

// Component to represent a single User 'Card' 
// (note: this is a function component so cannot use state)
function User(props) {
    return (
<div className="column is-3">
        <div className="card" >
          <div className="card-image">
            <figure className="image is-4by3">
              <img alt='Profile' src={props.image}></img>
            </figure>
          </div>
          <div className="card-content">
            <div className="media">
              <div className="media-content">
                <p className="title is-4">{props.name}</p>
                <p className="subtitle">{props.last}</p>
                <i className="subtitle">{props.quote}</i>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
}

// Iterate across all elements (u) in the users array, producing a User component for each
// Data is passed into the components via props
const userList = users.map(u => (
    <User
        key={u.name.first}
        name={u.name.first}
        last={u.name.last}
        image={u.picture.medium}
        quote={u.quote}
    />
));

// Render all the User components in a 'grid' layout
ReactDOM.render(<div className="columns is-multiline">
    {userList}
  </div>, document.getElementById("root"));
